<?php

namespace App\Controllers;

use App\Controllers\SystemMessageController;

use Exception;

class TokenCsrfController extends BaseController
{
    private $uri;
    private $message;

    public function __construct()
    {
        $this->uri = new \CodeIgniter\HTTP\URI(current_url());
        $this->message = new SystemMessageController();
    }
    #
    public function index($parameter = NULL)
    {
        exit('403 Forbidden - Directory access is forbidden.');
    }

    # use App\Controllers\TokenCsrfController;
    # private $tokenCsrf;
    # $this->tokenCsrf = new TokenCsrfController();
    # $this->tokenCsrf->token_csrf();
    public function token_csrf()
    {
        try {
            $token_csrf = myUCI();
            session()->set('token_csrf',  $token_csrf);
            session()->markAsTempdata('token_csrf', 1800);
            return $token_csrf;
        } catch (\Exception $e) {
            $this->message->message(['ERRO: ' . $e->getMessage()], 'danger');
        }
        return $token_csrf;
    }

    # use App\Controllers\TokenCsrfController;
    # private $tokenCsrf;
    # $this->tokenCsrf = new TokenCsrfController();
    # $this->tokenCsrf->valid_token_csrf();
    public function valid_token_csrf($tokenCsrf)
    {
        try {
            if ($tokenCsrf == '') {
                // myPrint($tokenCsrf, 'src\app\Controllers\TokenCsrfController.php');
                return true;
            }
            if (session()->get('token_csrf')) {
                $token_csrf = session()->get('token_csrf');
            } else {
                $token_csrf = false;
                return false;
            }
            if ($token_csrf == $tokenCsrf) {
                return true;
            } else {
                return false;
            }
        } catch (\Exception $e) {
            $this->message->message(['ERRO: ' . $e->getMessage()], 'danger');
            return false;
        }
    }
}
